--
-- Structure de la table `devis`
--

DROP TABLE IF EXISTS `devis`;
CREATE TABLE IF NOT EXISTS `devis` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `prenom` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `nom` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `telephone` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `sexe` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `pays` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `type_chirurgie` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `autre_details` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `chirurgie` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `rappel` datetime NOT NULL,
  `moyen_rappel` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `commentaires` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `age` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `anticidMedicaux` longtext COLLATE utf8_unicode_ci,
  `anticidMedicauxExist` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Déchargement des données de la table `devis`
